package com.ipartek.formacion.ejemplocapas.controladores;

import java.io.IOException;
import java.util.ArrayList;

import javax.servlet.ServletException;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

import com.ipartek.formacion.modelo.pojos.Perro;

/**
 * Servlet implementation class PerrosServlet
 */
public class PerrosServlet extends HttpServlet {
	private static final long serialVersionUID = 1L;
       


	/**
	 * @see HttpServlet#doGet(HttpServletRequest request, HttpServletResponse response)
	 */
	protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
		ArrayList<Perro> perros = new ArrayList<>();
		
		Perro p1 = new Perro(1L, "Shih Tzu", "República Popular China, Tíbet", "https://petyzoo.com/wp-content/uploads/2017/10/shih-tzu-razas-perros-cuidados-alimentacion.jpg");
		Perro p2 = new Perro(2L, "Golden retriever", "Reino Unido, Escocia, Inglaterra", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRn2vuFbLoH5pvd-9bsAtNX1ddZVnS2fU4TBrgA6eX_m5YGlqzusw");
		Perro p3 = new Perro(3L, "Husky siberiano", "Siberia", "data:image/jpeg;base64,");
		
		perros.add(p1);
		perros.add(p2);
		perros.add(p3);
	
		request.setAttribute("perros", perros);
		request.getRequestDispatcher("perros.jsp").forward(request, response);
	
	
	
	}

	/**
	 * @see HttpServlet#doPost(HttpServletRequest request, HttpServletResponse response)
	 */
	protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
		
		doGet(request, response);
	}

}
