package com.ipartek.formacion.controladores;

import java.io.IOException;
import java.util.ArrayList;

import javax.servlet.ServletConfig;
import javax.servlet.ServletException;
import javax.servlet.annotation.WebServlet;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.validation.Validation;
import javax.validation.Validator;
import javax.validation.ValidatorFactory;

import com.ipartek.formacion.modelo.pojos.Retro;

/**
 * Servlet implementation class DiscoController
 */
@WebServlet("/retro")
public class RetroController extends HttpServlet {
	private static final long serialVersionUID = 1L;

	private ArrayList<Retro> retro;
	private ValidatorFactory factory;
	private Validator validator;

	@Override
	public void init(ServletConfig config) throws ServletException {
		super.init(config);

		factory = Validation.buildDefaultValidatorFactory();
		validator = factory.getValidator();

		retro = new ArrayList<Retro>();

		retro.add(new Retro(1l, "Last Armageddon", "PC", "1990", "https://upload.wikimedia.org/wikipedia/en/thumb/e/ea/Last_Armageddon.jpg/220px-Last_Armageddon.jpg"));
		retro.add(new Retro(2l, "Kirby", "GameBoy", "1992", "data:image/jpeg;base64,"));
		retro.add(new Retro(3l, "Super Mario Land", "GameBoy", "1989", "https://generacionnintendo.com/wp-content/uploads/2018/02/super-mario-land.jpg"));
		retro.add(new Retro(4l, "Battle City", "NES", "1985", "https://upload.wikimedia.org/wikipedia/en/thumb/4/48/Battle_City_NES_cover.jpg/270px-Battle_City_NES_cover.jpg"));
		retro.add(new Retro(5l, "Boggy 84", "MSX", "1983", "https://i.imgur.com/kmhAnyp.jpg"));
	}

	protected void doGet(HttpServletRequest request, HttpServletResponse response)
			throws ServletException, IOException {

		doPost(request, response);
	}

	protected void doPost(HttpServletRequest request, HttpServletResponse response)
			throws ServletException, IOException {

		request.setAttribute("retro", retro);

		request.getRequestDispatcher("retro.jsp").forward(request, response);
	}
}
